dw:transform-message.dw:set-payload:
%dw 1.0
%output application/json
---
{
	url: payload.url,
	menu: {
		url: payload.menu.url,
		dishes: payload.menu.dishes map ((dish , indexOfDish) -> {
			url: dish.url,
			name: dish.name,
			price: dish.price
		})
	},
	address: {
		url: payload.address.url,
		id: payload.address.id,
		city: payload.address.city,
		street: payload.address.street,
		address_num: payload.address.address_num,
		door_num: payload.address.door_num
	}
}
